export class AuthHelper {
    constructor(token, apiUrl) {
        this.token = token;
        this.apiUrl = apiUrl;
    }
    setToken(token) {
        this.token = token;
    }
    getToken() {
        return this.token;
    }
    getClientConfig() {
        return {
            apiUrl: this.apiUrl,
            token: this.token
        };
    }
}
